var count = 10;

var assignIt = function(val, callBackFun) {
this.count = val;
callBackFun(this.count + "is current value");

if(val > 10) {
	callBackFun(this.count + " is greater than 10");
} else {
	callBackFun(this.count + " is less than 10");
}

}

module.exports.count = count;
module.exports.assignIt = assignIt;